import React from 'react';

interface Agent {
  id: string;
  name: string;
  status: 'online' | 'offline' | 'busy';
  activeSessions: number;
  waitTime: number; // in seconds
  productivity: number; // percentage
}

interface AgentCardProps {
  agent: Agent;
}

const AgentCard: React.FC<AgentCardProps> = ({ agent }) => {
  const statusColor = {
    online: 'bg-green-500',
    offline: 'bg-gray-300',
    busy: 'bg-yellow-500',
  };

  return (
    <div className="max-w-sm rounded overflow-hidden shadow-lg p-4 bg-white">
      <div className={`w-3 h-3 rounded-full ${statusColor[agent.status]} mb-2`} />
      <h2 className="text-xl font-bold mb-2">{agent.name}</h2>
      <p className="text-gray-700 mb-1">Status: <span className="font-semibold">{agent.status}</span></p>
      <p className="text-gray-700 mb-1">Actieve Sessies: <span className="font-semibold">{agent.activeSessions}</span></p>
      <p className="text-gray-700 mb-1">Wachttijd: <span className="font-semibold">{agent.waitTime} seconden</span></p>
      <p className="text-gray-700 mb-1">Productiviteit: <span className="font-semibold">{agent.productivity}%</span></p>
    </div>
  );
};

export default AgentCard;
